import{_ as a,c as e}from"./app.40df414d.js";var o="/doc/assets/README-1639794969845.f51e0d7c.png",p="/doc/assets/README-1639796305213.47faf155.png",r="/doc/assets/README-1639799311845.e0b36778.png";const d={},i=e('<h1 id="_3-apache-hadoop\u7684\u91CD\u8981\u7EC4\u6210-\u3010\u719F\u6089\u3011" tabindex="-1"><a class="header-anchor" href="#_3-apache-hadoop\u7684\u91CD\u8981\u7EC4\u6210-\u3010\u719F\u6089\u3011" aria-hidden="true">#</a> 3. Apache Hadoop\u7684\u91CD\u8981\u7EC4\u6210 \u3010\u719F\u6089\u3011</h1><blockquote><p>Hadoop = HDFS(\u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF) + MapReduce(\u5206\u5E03\u5F0F\u8BA1\u7B97\u6846\u67B6) + Yarn(\u8D44\u6E90\u534F\u8C03\u6846\u67B6) + Common\u6A21\u5757</p></blockquote><h2 id="_3-1-hadoop-hdfs" tabindex="-1"><a class="header-anchor" href="#_3-1-hadoop-hdfs" aria-hidden="true">#</a> 3.1 Hadoop HDFS</h2><p>HDFS(Hadoop Distribute File System )\u662F\u4E00\u4E2A\u9AD8\u53EF\u9760\u3001\u9AD8\u541E\u5410\u91CF\u7684\u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF</p><p>\u6BD4\u5982:\u9700\u8981\u5B9E\u73B0100T\u6570\u636E\u5B58\u50A8\uFF0C\u600E\u4E48\u529E\uFF1F</p><p>\u201C\u5206\u800C\u6CBB\u4E4B\u201D\u3002\u5206\uFF0C\u5373\u62C6\u5206 -&gt; \u6570\u636E\u5207\u5272\uFF0C100T\u6570\u636E\u62C6\u5206\u4E3A10G\u4E00\u4E2A\u6570\u636E\u5757\u7531\u4E00\u4E2A\u7535\u8111\u8282\u70B9\u5B58\u50A8\u8FD9\u4E2A\u6570\u636E\u5757\u3002</p><blockquote><p>\u6838\u5FC3\u601D\u60F3\uFF1A\u6570\u636E\u5207\u5272\u3001\u5236\u4F5C\u526F\u672C\u3001\u5206\u6563\u50A8\u5B58</p></blockquote><p><img src="'+o+'" alt="HDFS\u67B6\u6784\u7B80\u56FE"></p><p>\u56FE\u4E2D\u6D89\u53CA\u5230\u51E0\u4E2A\u89D2\u8272</p><ul><li>NameNode(nn): \u5B58\u50A8\u6587\u4EF6\u7684\u5143\u6570\u636E\uFF0C\u6BD4\u5982\u6587\u4EF6\u540D\u3001\u6587\u4EF6\u76EE\u5F55\u7ED3\u6784\u3001\u6587\u4EF6\u5C5E\u6027(\u751F\u6210\u65F6\u95F4\u3001\u526F\u672C\u6570\u3001\u6587\u4EF6\u6743\u9650)\uFF0C\u4EE5\u53CA\u6BCF\u4E2A\u6587\u4EF6\u7684\u5757\u5217\u8868\u548C\u5757\u6240\u5728\u7684DataNode\u7B49\u3002</li><li>SecondaryNameNode(2nn): \u8F85\u52A9NameNode\u66F4\u597D\u7684\u5DE5\u4F5C\uFF0C\u7528\u6765\u76D1\u63A7HDFS\u72B6\u6001\u7684\u8F85\u52A9\u540E\u53F0\u7A0B\u5E8F\uFF0C\u6BCF\u9694\u4E00\u6BB5\u65F6\u95F4\u83B7\u53D6HDFS\u5143\u6570\u636E\u5FEB\u7167\u3002</li><li>DataNode(dn): \u5728\u672C\u5730\u6587\u4EF6\u7CFB\u7EDF\u5B58\u50A8\u6587\u4EF6\u5757\u6570\u636E\uFF0C\u4EE5\u53CA\u5757\u6570\u636E\u7684\u6821\u9A8C</li></ul><blockquote><p>\u6CE8\u610F: NN\uFF0C2NN, DN\u8FD9\u4E9B\u65E2\u662F\u89D2\u8272\u540D\u79F0\uFF0C\u4E5F\u662F\u8FDB\u7A0B\u540D\u79F0\uFF0C\u4E5F\u4EE3\u6307\u7535\u8111\u8282\u70B9!!!</p></blockquote><h2 id="_3-2-hadoop-mapreduce" tabindex="-1"><a class="header-anchor" href="#_3-2-hadoop-mapreduce" aria-hidden="true">#</a> 3.2 Hadoop MapReduce</h2><p>\u4E00\u4E2A\u5206\u5E03\u5F0F\u7684\u79BB\u7EBF\u5E76\u884C\u8BA1\u7B97\u6846\u67B6\u3002</p><blockquote><p>\u6838\u5FC3\u601D\u60F3\uFF1A\u62C6\u89E3\u4EFB\u52A1\u3001\u5206\u6563\u5904\u7406\u3001\u6C47\u6574\u7ED3\u679C</p></blockquote><p><strong>MapReduce\u8BA1\u7B97 = Map\u9636\u6BB5 + Reduce\u9636\u6BB5</strong></p><ul><li>Map\u9636\u6BB5\u5C31\u662F\u201C\u5206\u201D\u7684\u9636\u6BB5\uFF0C\u5E76\u884C\u5904\u7406\u8F93\u5165\u6570\u636E;</li><li>Reduce\u9636\u6BB5\u5C31\u662F\u201C\u5408\u201D\u7684\u9636\u6BB5\uFF0C\u5BF9Map\u9636\u6BB5\u7ED3\u679C\u8FDB\u884C\u6C47\u603B;</li></ul><p><img src="'+p+'" alt="MR\u57FA\u672C\u601D\u60F3\u56FE\u89E3"></p><h2 id="_3-3-hadoop-yarn" tabindex="-1"><a class="header-anchor" href="#_3-3-hadoop-yarn" aria-hidden="true">#</a> 3.3 Hadoop YARN</h2><p>\u4F5C\u4E1A\u8C03\u5EA6\u4E0E\u96C6\u7FA4\u8D44\u6E90\u7BA1\u7406\u7684\u6846\u67B6</p><p>\u8BA1\u7B97\u8D44\u6E90\u534F\u8C03</p><p><img src="'+r+'" alt="YARN\u67B6\u6784\u7B80\u4ECB"></p><p>Yarn\u4E2D\u6709\u5982\u4E0B\u51E0\u4E2A\u4E3B\u8981\u89D2\u8272:</p><blockquote><p>\u540C\u6837\uFF0C\u65E2\u662F\u89D2\u8272\u540D\u3001\u4E5F\u662F\u8FDB\u7A0B\u540D\uFF0C\u4E5F\u6307\u4EE3\u6240\u5728\u8BA1\u7B97\u673A\u8282\u70B9\u540D\u79F0!</p></blockquote><ul><li>ResourceManager(rm): \u5904\u7406\u5BA2\u6237\u7AEF\u8BF7\u6C42\u3001\u542F\u52A8/\u76D1\u63A7ApplicationMaster\u3001\u76D1\u63A7NodeManager\u3001\u8D44\u6E90\u5206\u914D\u4E0E\u8C03\u5EA6;</li><li>NodeManager(nm): \u5355\u4E2A\u8282\u70B9\u4E0A\u7684\u8D44\u6E90\u7BA1\u7406\u3001\u5904\u7406\u6765\u81EAResourceManager\u7684\u547D\u4EE4\u3001\u5904\u7406\u6765\u81EAApplicationMaster\u7684\u547D\u4EE4;</li><li>ApplicationMaster(am): \u6570\u636E\u5207\u5206\u3001\u4E3A\u5E94\u7528\u7A0B\u5E8F\u7533\u8BF7\u8D44\u6E90\uFF0C\u5E76\u5206\u914D\u7ED9\u5185\u90E8\u4EFB\u52A1\u3001\u4EFB\u52A1\u76D1\u63A7\u4E0E\u5BB9\u9519\u3002</li><li>Container:\u5BF9\u4EFB\u52A1\u8FD0\u884C\u73AF\u5883\u7684\u62BD\u8C61\uFF0C\u5C01\u88C5\u4E86CPU\u3001\u5185\u5B58\u7B49\u591A\u7EF4\u8D44\u6E90\u4EE5\u53CA\u73AF\u5883\u53D8\u91CF\u3001\u542F\u52A8\u547D\u4EE4\u7B49\u4EFB\u52A1\u8FD0\u884C\u76F8\u5173\u7684\u4FE1\u606F\u3002</li></ul><p><strong>ResourceManager\u662F\u8001\u5927\uFF0CNodeManager\u662F\u5C0F\u5F1F\uFF0CApplicationMaster\u662F\u8BA1\u7B97\u4EFB\u52A1\u4E13\u5458\u3002</strong></p><h2 id="_3-4-hadoop-common" tabindex="-1"><a class="header-anchor" href="#_3-4-hadoop-common" aria-hidden="true">#</a> 3.4 Hadoop Common</h2><p>\u652F\u6301\u5176\u4ED6\u6A21\u5757\u7684\u5DE5\u5177\u6A21\u5757(Configuration\u3001RPC\u3001\u5E8F\u5217\u5316\u673A\u5236\u3001\u65E5\u5FD7\u64CD\u4F5C)</p>',27);function t(n,c){return i}var h=a(d,[["render",t],["__file","index.html.vue"]]);export{h as default};
