import{_ as e,c as i}from"./app.40df414d.js";var l="/doc/assets/README-1644842449379.7292ef6c.png";const a={},r=i('<blockquote><p>\u6570\u636E\u4ED3\u5E93\u5DE5\u5177</p></blockquote><ul><li>\u6570\u636E\u4ED3\u5E93\u5DE5\u5177\uFF1AHive \u3010\u91CD\u70B9\u3011</li><li>\u6570\u636E\u4EA4\u4E92\u5DE5\u5177\uFF1AHue</li><li>\u6570\u636E\u91C7\u96C6\u5DE5\u5177\uFF1AFlume</li><li>\u6570\u636E\u8FC1\u79FB\u5DE5\u5177\uFF1ASqoop</li></ul><h1 id="_1-hive\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#_1-hive\u6982\u8FF0" aria-hidden="true">#</a> 1. Hive\u6982\u8FF0</h1><ul><li>HDFS =&gt; \u6D77\u91CF\u6570\u636E\u7684\u5B58\u50A8</li><li>MapReduce =&gt; \u6D77\u91CF\u6570\u636E\u7684\u5206\u6790\u548C\u5904\u7406</li><li>YARN =&gt; \u96C6\u7FA4\u8D44\u6E90\u7684\u7BA1\u7406\u548C\u4F5C\u4E1A\u8C03\u5EA6</li></ul><h2 id="_1-1-hive\u4EA7\u751F\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#_1-1-hive\u4EA7\u751F\u80CC\u666F" aria-hidden="true">#</a> 1.1 Hive\u4EA7\u751F\u80CC\u666F</h2><p>\u76F4\u63A5\u4F7F\u7528 MapReduce \u5904\u7406\u5927\u6570\u636E\uFF0C\u5C06\u9762\u4E34\u4EE5\u4E0B\u95EE\u9898:</p><ul><li>MapReduce \u5F00\u53D1\u96BE\u5EA6\u5927\uFF0C\u5B66\u4E60\u6210\u672C\u9AD8(wordCount =&gt; Hello World)</li><li>HDFS \u6587\u4EF6\u6CA1\u6709\u5B57\u6BB5\u540D\u3001\u6CA1\u6709\u6570\u636E\u7C7B\u578B\uFF0C\u4E0D\u65B9\u4FBF\u8FDB\u884C\u6570\u636E\u7684\u6709\u6548\u7BA1\u7406</li><li>\u4F7F\u7528 MapReduce \u6846\u67B6\u5F00\u53D1\uFF0C\u9879\u76EE\u5468\u671F\u957F\uFF0C\u6210\u672C\u9AD8</li></ul><p>Hive \u662F\u57FA\u4E8E Hadoop \u7684\u4E00\u4E2A\u6570\u636E\u4ED3\u5E93\u5DE5\u5177\uFF0C\u53EF\u4EE5\u5C06\u7ED3\u6784\u5316\u7684\u6570\u636E\u6587\u4EF6\u6620\u5C04\u4E3A\u4E00\u5F20\u8868(\u7C7B\u4F3C\u4E8ERDBMS\u4E2D\u7684\u8868)\uFF0C\u5E76\u63D0\u4F9B\u7C7BSQL\u67E5\u8BE2\u529F\u80FD; Hive \u662F\u7531 Facebook \u5F00\u6E90\uFF0C\u7528\u4E8E\u89E3\u51B3\u6D77\u91CF\u7ED3\u6784\u5316\u65E5\u5FD7\u7684\u6570\u636E\u7EDF\u8BA1\u3002</p><ul><li>Hive \u672C\u8D28\u662F\u5C06 SQL \u8F6C\u6362\u4E3A MapReduce \u7684\u4EFB\u52A1\u8FDB\u884C\u8FD0\u7B97</li><li>\u5E95\u5C42\u7531 HDFS \u6765\u63D0\u4F9B\u6570\u636E\u5B58\u50A8</li><li>\u53EF\u4EE5\u5C06 Hive \u7406\u89E3\u4E3A\uFF1A\u4E00\u4E2A<em>\u5C06 SQL \u8F6C\u6362\u4E3A MapReduce \u4EFB\u52A1\u7684\u5DE5\u5177</em></li></ul><p>\u6570\u636E\u4ED3\u5E93(Data Warehouse)\u662F\u4E00\u4E2A\u9762\u5411\u4E3B\u9898\u7684\u3001\u96C6\u6210\u7684\u3001\u76F8\u5BF9\u7A33\u5B9A\u7684\u3001\u53CD\u6620\u5386\u53F2\u53D8\u5316\u7684\u6570\u636E\u96C6\u5408\uFF0C\u4E3B\u8981\u7528\u4E8E\u7BA1\u7406\u51B3\u7B56\u3002 (\u6570\u636E\u4ED3\u5E93\u4E4B\u7236\u6BD4\u5C14\xB7\u6069\u95E8\uFF0C1991\u5E74\u63D0\u51FA)\u3002</p><ul><li>\u6570\u636E\u4ED3\u5E93\u7684\u76EE\u7684: \u6784\u5EFA\u9762\u5411\u5206\u6790\u7684\u3001\u96C6\u6210\u7684\u6570\u636E\u96C6\u5408; \u4E3A\u4F01\u4E1A\u63D0\u4F9B\u51B3\u7B56\u652F\u6301</li><li>\u6570\u636E\u4ED3\u5E93\u672C\u8EAB\u4E0D\u4EA7\u751F\u6570\u636E\uFF0C\u6570\u636E\u6765\u6E90\u4E8E\u5916\u90E8</li><li>\u5B58\u50A8\u4E86\u5927\u91CF\u6570\u636E\uFF0C\u5BF9\u8FD9\u4E9B\u6570\u636E\u7684\u5206\u6790\u548C\u5904\u7406\u4E0D\u53EF\u907F\u514D\u7684\u7528\u5230 Hive</li></ul><h2 id="_1-2-hive-\u548C-rdbms-\u5BF9\u6BD4" tabindex="-1"><a class="header-anchor" href="#_1-2-hive-\u548C-rdbms-\u5BF9\u6BD4" aria-hidden="true">#</a> 1.2 Hive \u548C RDBMS \u5BF9\u6BD4</h2><blockquote><p>Relational Database Management System\uFF1ARDBMS</p></blockquote><p>\u7531\u4E8E Hive \u91C7\u7528\u4E86\u7C7B\u4F3C SQL \u7684\u67E5\u8BE2\u8BED\u8A00 HQL(Hive Query Language)\uFF0C\u56E0\u6B64\u5F88\u5BB9\u6613\u5C06 Hive \u7406\u89E3\u4E3A\u6570\u636E\u5E93\u3002 \u5176\u5B9E\u4ECE\u7ED3\u6784\u4E0A\u6765\u770B\uFF0CHive \u548C\u4F20\u7EDF\u7684\u5173\u7CFB\u6570\u636E\u5E93\u9664\u4E86\u62E5\u6709\u7C7B\u4F3C\u7684\u67E5\u8BE2\u8BED\u8A00\uFF0C\u518D\u65E0\u7C7B\u4F3C\u4E4B\u5904\u3002</p><ul><li>\u67E5\u8BE2\u8BED\u8A00\u76F8\u4F3C</li></ul><p>HQL \u4E0E SQL \u9AD8\u5EA6\u76F8\u4F3C\u3002</p><p>\u7531\u4E8ESQL\u88AB\u5E7F\u6CDB\u7684\u5E94\u7528\u5728\u6570\u636E\u4ED3\u5E93\u4E2D\uFF0C\u56E0\u6B64\uFF0C\u4E13\u95E8\u9488\u5BF9Hive\u7684\u7279\u6027\u8BBE\u8BA1\u4E86\u7C7BSQL\u7684\u67E5\u8BE2\u8BED\u8A00HQL\u3002\u719F\u6089SQL\u5F00\u53D1\u7684\u5F00\u53D1\u8005\u53EF\u4EE5\u5F88\u65B9\u4FBF\u7684\u4F7F\u7528Hive\u8FDB\u884C\u5F00\u53D1\u3002</p><ul><li>\u6570\u636E\u89C4\u6A21\u3002</li></ul><p>Hive\u5B58\u50A8\u6D77\u91CF\u6570\u636E; RDBMS\u53EA\u80FD\u5904\u7406\u6709\u9650\u7684\u6570\u636E\u96C6;</p><p>\u7531\u4E8EHive\u5EFA\u7ACB\u5728\u96C6\u7FA4\u4E0A\u5E76\u53EF\u4EE5\u5229\u7528MapReduce\u8FDB\u884C\u5E76\u884C\u8BA1\u7B97\uFF0C\u56E0\u6B64\u53EF\u4EE5\u652F\u6301\u5F88\u5927\u89C4\u6A21\u7684\u6570\u636E; \u800CRDBMS\u53EF\u4EE5\u652F\u6301\u7684\u6570\u636E\u89C4\u6A21\u8F83\u5C0F\u3002</p><ul><li>\u6267\u884C\u5F15\u64CE</li></ul><p>Hive \u7684\u5F15\u64CE\u662F MR/Tez/Spark/Flink; RDBMS \u4F7F\u7528\u81EA\u5DF1\u7684\u6267\u884C\u5F15\u64CE</p><p>Hive \u4E2D\u5927\u591A\u6570\u67E5\u8BE2\u7684\u6267\u884C\u662F\u901A\u8FC7 Hadoop \u63D0\u4F9B\u7684 MapReduce \u6765\u5B9E\u73B0\u7684\u3002\u800CRDBMS\u901A\u5E38\u6709\u81EA\u5DF1\u7684\u6267\u884C\u5F15\u64CE\u3002</p><ul><li>\u6570\u636E\u5B58\u50A8</li></ul><p>Hive \u4FDD\u5B58\u5728 HDFS \u4E0A; RDBMS \u4FDD\u5B58\u5728\u672C\u5730\u6587\u4EF6\u7CFB\u7EDF\u6216\u88F8\u8BBE\u5907</p><p>Hive \u7684\u6570\u636E\u90FD\u662F\u5B58\u50A8\u5728 HDFS \u4E2D\u7684\u3002\u800CRDBMS\u662F\u5C06\u6570\u636E\u4FDD\u5B58\u5728\u672C\u5730\u6587\u4EF6\u7CFB\u7EDF\u6216\u88F8\u8BBE\u5907\u4E2D\u3002</p><ul><li>\u6267\u884C\u901F\u5EA6</li></ul><p>Hive \u76F8\u5BF9\u6162(MR/\u6570\u636E\u91CF); RDBMS\u76F8\u5BF9\u5FEB;</p><p>Hive \u5B58\u50A8\u7684\u6570\u636E\u91CF\u5927\uFF0C\u5728\u67E5\u8BE2\u6570\u636E\u7684\u65F6\u5019\uFF0C\u901A\u5E38\u6CA1\u6709\u7D22\u5F15\uFF0C\u9700\u8981\u626B\u63CF\u6574\u4E2A\u8868; \u52A0\u4E4B Hive \u4F7F\u7528 MapReduce \u4F5C\u4E3A\u6267\u884C\u5F15\u64CE\uFF0C\u8FD9\u4E9B\u56E0\u7D20\u90FD\u4F1A\u5BFC\u81F4\u8F83\u9AD8\u7684\u5EF6\u8FDF\u3002 \u800C RDBMS \u5BF9\u6570\u636E\u7684\u8BBF\u95EE\u901A\u5E38\u662F\u57FA\u4E8E\u7D22\u5F15\u7684\uFF0C\u6267\u884C\u5EF6\u8FDF\u8F83\u4F4E\u3002\u5F53\u7136\u8FD9\u4E2A\u4F4E\u662F\u6709\u6761\u4EF6\u7684\uFF0C\u5373\u6570\u636E\u89C4\u6A21\u8F83\u5C0F\uFF0C\u5F53\u6570\u636E\u89C4\u6A21\u5927\u5230\u8D85\u8FC7\u6570\u636E\u5E93\u7684\u5904\u7406\u80FD\u529B\u7684\u65F6\u5019\uFF0CHive \u7684\u5E76\u884C\u8BA1\u7B97\u663E\u7136\u80FD\u4F53\u73B0\u51FA\u5E76\u884C\u7684\u4F18\u52BF\u3002</p><ul><li>\u53EF\u6269\u5C55\u6027</li></ul><p>Hive \u652F\u6301\u6C34\u5E73\u6269\u5C55; \u901A\u5E38 RDBMS \u652F\u6301\u5782\u76F4\u6269\u5C55\uFF0C\u5BF9\u6C34\u5E73\u6269\u5C55\u4E0D\u53CB\u597D</p><p>Hive \u5EFA\u7ACB\u5728 Hadoop \u4E4B\u4E0A\uFF0C\u5176\u53EF\u6269\u5C55\u6027\u4E0EHadoop \u7684\u53EF\u6269\u5C55\u6027\u662F\u4E00\u81F4\u7684(Hadoop\u96C6\u7FA4\u89C4\u6A21\u53EF\u4EE5\u8F7B\u677E\u8D85\u8FC71000\u4E2A\u8282\u70B9)\u3002 \u800C RDBMS \u7531\u4E8E ACID \u8BED\u4E49\u7684\u4E25\u683C\u9650\u5236\uFF0C\u6269\u5C55\u884C\u975E\u5E38\u6709\u9650\u3002\u76EE\u524D\u6700\u5148\u8FDB\u7684\u5E76\u884C\u6570\u636E\u5E93 Oracle \u5728\u7406\u8BBA\u4E0A\u7684\u6269\u5C55\u80FD\u529B\u4E5F\u53EA\u6709100\u53F0\u5DE6\u53F3\u3002</p><ul><li>\u6570\u636E\u66F4\u65B0</li></ul><p>Hive \u5BF9\u6570\u636E\u66F4\u65B0\u4E0D\u53CB\u597D; RDBMS \u652F\u6301\u9891\u7E41\u3001\u5FEB\u901F\u6570\u636E\u66F4\u65B0</p><p>Hive \u662F\u9488\u5BF9\u6570\u636E\u4ED3\u5E93\u5E94\u7528\u8BBE\u8BA1\u7684\uFF0C\u6570\u636E\u4ED3\u5E93\u7684\u5185\u5BB9\u662F\u8BFB\u591A\u5199\u5C11\u7684\u3002\u56E0\u6B64\uFF0CHive \u4E2D\u4E0D\u5EFA\u8BAE\u5BF9\u6570\u636E\u6539\u5199\uFF0C\u6240\u6709\u7684\u6570\u636E\u90FD\u662F\u5728\u52A0\u8F7D\u7684\u65F6\u5019\u786E\u5B9A\u597D\u7684\u3002 \u800CRDBMS\u4E2D\u7684\u6570\u636E\u9700\u8981\u9891\u7E41\u3001\u5FEB\u901F\u7684\u8FDB\u884C\u66F4\u65B0\u3002</p><h2 id="_1-3-hive\u7684\u4F18\u7F3A\u70B9" tabindex="-1"><a class="header-anchor" href="#_1-3-hive\u7684\u4F18\u7F3A\u70B9" aria-hidden="true">#</a> 1.3 Hive\u7684\u4F18\u7F3A\u70B9</h2><h3 id="_1-3-1-hive\u7684\u4F18\u70B9" tabindex="-1"><a class="header-anchor" href="#_1-3-1-hive\u7684\u4F18\u70B9" aria-hidden="true">#</a> 1.3.1 Hive\u7684\u4F18\u70B9</h3><ul><li>\u5B66\u4E60\u6210\u672C\u4F4E</li></ul><p>Hive\u63D0\u4F9B\u4E86\u7C7B\u4F3CSQL\u7684\u67E5\u8BE2\u8BED\u8A00\uFF0C\u5F00\u53D1\u4EBA\u5458\u80FD\u5FEB\u901F\u4E0A\u624B;</p><ul><li>\u5904\u7406\u6D77\u91CF\u6570\u636E</li></ul><p>\u5E95\u5C42\u6267\u884C\u7684\u662F MapReduce \u4EFB\u52A1;</p><ul><li>\u7CFB\u7EDF\u53EF\u4EE5\u6C34\u5E73\u6269\u5C55</li></ul><p>\u5E95\u5C42\u57FA\u4E8EHadoop;</p><ul><li>\u529F\u80FD\u53EF\u4EE5\u6269\u5C55</li></ul><p>Hive\u5141\u8BB8\u7528\u6237\u81EA\u5B9A\u4E49\u51FD\u6570;</p><ul><li>\u826F\u597D\u7684\u5BB9\u9519\u6027</li></ul><p>\u67D0\u4E2A\u8282\u70B9\u53D1\u751F\u6545\u969C\uFF0CHQL\u4ECD\u7136\u53EF\u4EE5\u6B63\u5E38\u5B8C\u6210;</p><ul><li>\u7EDF\u4E00\u7684\u5143\u6570\u636E\u7BA1\u7406</li></ul><p>\u5143\u6570\u636E\u5305\u62EC:\u6709\u54EA\u4E9B\u8868\u3001\u8868\u6709\u4EC0\u4E48\u5B57\u6BB5\u3001\u5B57\u6BB5\u662F\u4EC0\u4E48\u7C7B\u578B</p><h3 id="_1-3-2-hive\u7684\u7F3A\u70B9" tabindex="-1"><a class="header-anchor" href="#_1-3-2-hive\u7684\u7F3A\u70B9" aria-hidden="true">#</a> 1.3.2 Hive\u7684\u7F3A\u70B9</h3><ul><li>HQL\u8868\u8FBE\u80FD\u529B\u6709\u9650;</li><li>\u8FED\u4EE3\u8BA1\u7B97\u65E0\u6CD5\u8868\u8FBE;</li><li>Hive\u7684\u6267\u884C\u6548\u7387\u4E0D\u9AD8(\u57FA\u4E8EMR\u7684\u6267\u884C\u5F15\u64CE);</li><li>Hive\u81EA\u52A8\u751F\u6210\u7684MapReduce\u4F5C\u4E1A\uFF0C\u67D0\u4E9B\u60C5\u51B5\u4E0B\u4E0D\u591F\u667A\u80FD;</li><li>Hive\u7684\u8C03\u4F18\u56F0\u96BE;</li></ul><h2 id="_1-4-hive\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_1-4-hive\u67B6\u6784" aria-hidden="true">#</a> 1.4 Hive\u67B6\u6784</h2><p><img src="'+l+'" alt="Hive\u903B\u8F91\u67B6\u6784\u56FE"></p><h3 id="_1-4-1-\u7528\u6237\u63A5\u53E3" tabindex="-1"><a class="header-anchor" href="#_1-4-1-\u7528\u6237\u63A5\u53E3" aria-hidden="true">#</a> 1.4.1 \u7528\u6237\u63A5\u53E3</h3><ul><li>CLI(Common Line Interface): Hive \u7684\u547D\u4EE4\u884C\uFF0C\u7528\u4E8E\u63A5\u6536 HQL\uFF0C\u5E76\u8FD4\u56DE\u7ED3\u679C;</li><li>JDBC/ODBC: \u662F\u6307 Hive \u7684 Java \u5B9E\u73B0\uFF0C\u4E0E\u4F20\u7EDF\u6570\u636E\u5E93JDBC\u7C7B\u4F3C;</li><li>WebUI: \u662F\u6307\u53EF\u901A\u8FC7\u6D4F\u89C8\u5668\u8BBF\u95EE Hive;</li></ul><h3 id="_1-4-2-thrift-server" tabindex="-1"><a class="header-anchor" href="#_1-4-2-thrift-server" aria-hidden="true">#</a> 1.4.2 Thrift Server</h3><p>Hive \u53EF\u9009\u7EC4\u4EF6\uFF0C\u662F\u4E00\u4E2A\u8F6F\u4EF6\u6846\u67B6\u670D\u52A1\uFF0C\u5141\u8BB8\u5BA2\u6237\u7AEF\u4F7F\u7528\u5305\u62EC Java\u3001C++\u3001Ruby \u548C\u5176\u4ED6\u5F88\u591A\u79CD\u8BED\u8A00\uFF0C\u901A\u8FC7\u7F16\u7A0B\u7684\u65B9\u5F0F\u8FDC\u7A0B\u8BBF\u95EE Hive;</p><h3 id="_1-4-3-\u5143\u6570\u636E\u7BA1\u7406-metastore" tabindex="-1"><a class="header-anchor" href="#_1-4-3-\u5143\u6570\u636E\u7BA1\u7406-metastore" aria-hidden="true">#</a> 1.4.3 \u5143\u6570\u636E\u7BA1\u7406(MetaStore)</h3><p>Hive \u5C06\u5143\u6570\u636E\u5B58\u50A8\u5728\u5173\u7CFB\u6570\u636E\u5E93\u4E2D(\u5982mysql\u3001 derby)\u3002</p><p>Hive\u7684\u5143\u6570\u636E\u5305\u62EC: \u6570\u636E\u5E93\u540D\u3001\u8868\u540D\u53CA\u7C7B\u578B\u3001\u5B57\u6BB5\u540D\u79F0\u53CA\u6570\u636E\u7C7B\u578B\u3001\u6570\u636E\u6240\u5728\u4F4D\u7F6E\u7B49;</p><h3 id="_1-4-4-\u9A71\u52A8\u7A0B\u5E8F-driver" tabindex="-1"><a class="header-anchor" href="#_1-4-4-\u9A71\u52A8\u7A0B\u5E8F-driver" aria-hidden="true">#</a> 1.4.4 \u9A71\u52A8\u7A0B\u5E8F(Driver)</h3><ul><li>\u89E3\u6790\u5668(SQLParser): \u4F7F\u7528\u7B2C\u4E09\u65B9\u5DE5\u5177(antlr)\u5C06HQL\u5B57\u7B26\u4E32\u8F6C\u6362\u6210\u62BD\u8C61\u8BED\u6CD5\u6811(AST); \u5BF9AST\u8FDB\u884C\u8BED\u6CD5\u5206\u6790\uFF0C\u6BD4\u5982\u5B57\u6BB5\u662F\u5426\u5B58\u5728\u3001SQL\u8BED\u4E49\u662F\u5426\u6709\u8BEF\u3001\u8868\u662F\u5426\u5B58\u5728;</li><li>\u7F16\u8BD1\u5668(Compiler): \u5C06\u62BD\u8C61\u8BED\u6CD5\u6811\u7F16\u8BD1\u751F\u6210\u903B\u8F91\u6267\u884C\u8BA1\u5212;</li><li>\u4F18\u5316\u5668(Optimizer): \u5BF9\u903B\u8F91\u6267\u884C\u8BA1\u5212\u8FDB\u884C\u4F18\u5316\uFF0C\u51CF\u5C11\u4E0D\u5FC5\u8981\u7684\u5217\u3001\u4F7F\u7528\u5206 \u533A\u7B49;</li><li>\u6267\u884C\u5668(Executor): \u628A\u903B\u8F91\u6267\u884C\u8BA1\u5212\u8F6C\u6362\u6210\u53EF\u4EE5\u8FD0\u884C\u7684\u7269\u7406\u8BA1\u5212;</li></ul>',62);function h(d,p){return r}var v=e(a,[["render",h],["__file","index.html.vue"]]);export{v as default};
