import{_ as l,c as i}from"./app.40df414d.js";const p={},a=i('<h1 id="_1-\u5927\u6570\u636E\u7B80\u4ECB\u3010\u4E86\u89E3\u3011" tabindex="-1"><a class="header-anchor" href="#_1-\u5927\u6570\u636E\u7B80\u4ECB\u3010\u4E86\u89E3\u3011" aria-hidden="true">#</a> 1. \u5927\u6570\u636E\u7B80\u4ECB\u3010\u4E86\u89E3\u3011</h1><p>\u5927\u6570\u636E\u6280\u672F\u89E3\u51B3\u7684\u662F\u4EC0\u4E48\u95EE\u9898?</p><p>\u5927\u6570\u636E\u6280\u672F\u89E3\u51B3\u7684\u4E3B\u8981\u662F\u6D77\u91CF\u6570\u636E\u7684\u5B58\u50A8\u548C\u8BA1\u7B97\u3002</p><p>Hadoop \u7684\u5E7F\u4E49\u548C\u72ED\u4E49\u4E4B\u5206</p><p>\u72ED\u4E49\u7684Hadoop: \u6307\u7684\u662F\u4E00\u4E2A\u6846\u67B6\u3002</p><ul><li>Hadoop\u662F\u7531\u4E09\u90E8\u5206\u7EC4\u6210:HDFS:\u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF--\u300B\u5B58\u50A8;</li><li>MapReduce:\u5206\u5E03\u5F0F\u79BB\u7EBF\u8BA1\u7B97\u6846\u67B6--\u300B\u8BA1\u7B97;</li><li>Yarn: \u8D44\u6E90\u8C03\u5EA6\u6846\u67B6</li></ul><p>\u5E7F\u4E49\u7684Hadoop: \u5E7F\u4E49Hadoop\u662F\u4E0D\u4EC5\u4EC5\u5305\u542BHadoop\u6846\u67B6\uFF0C\u9664\u4E86Hadoop\u6846\u67B6\u4E4B\u5916\u8FD8\u6709\u4E00\u4E9B\u8F85\u52A9\u6846\u67B6\u3002</p><ul><li>Flume: \u65E5\u5FD7\u6570\u636E\u91C7\u96C6</li><li>Sqoop: \u5173\u7CFB\u578B\u6570\u636E\u5E93\u6570\u636E\u7684\u91C7\u96C6;</li><li>Hive: \u6DF1\u5EA6\u4F9D\u8D56Hadoop\u6846\u67B6\u5B8C\u6210\u8BA1\u7B97(sql)</li><li>Hbase: \u5927\u6570\u636E\u9886\u57DF\u7684\u6570\u636E\u5E93(\u76F8\u5F53\u4E8Emysql)</li><li>Sqoop: \u6570\u636E\u7684\u5BFC\u51FA</li></ul><p><strong>\u5E7F\u4E49Hadoop\u6307\u7684\u662F\u4E00\u4E2A\u751F\u6001\u5708\u3002</strong></p><h2 id="\u4E3B\u8981\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u4E3B\u8981\u5185\u5BB9" aria-hidden="true">#</a> \u4E3B\u8981\u5185\u5BB9</h2><ol><li>\u5927\u6570\u636E\u7B80\u4ECB(\u5B9A\u4E49\uFF0C\u7279\u70B9\uFF0C\u5E94\u7528\u573A\u666F\uFF0C\u53D1\u5C55\u8D8B\u52BF\uFF0C\u804C\u4E1A\u53D1\u5C55\u8DEF\u7EBF)</li><li>Hadoop\u7B80\u4ECB(\u5386\u53F2\uFF0C\u53D1\u5C55\u8DEF\u7EBF-\u7248\u672C\u53D8\u66F4\uFF0C\u53D1\u884C\u7248(CDH))</li><li>Hadoop\u7684\u91CD\u8981\u7EC4\u6210(hdfs,mapreduce,yarn)</li><li>Apache Hadoop \u5B8C\u5168\u5206\u5E03\u5F0F\u96C6\u7FA4\u642D\u5EFA</li><li>HDFS \u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF(\u67B6\u6784\uFF0C\u539F\u7406\uFF0C\u5E38\u7528api,\u5143\u6570\u636E\u7BA1\u7406\uFF0C\u6743\u9650\uFF0C\u65E5\u5FD7\u91C7\u96C6\u7684\u7EFC\u5408\u6848\u4F8B)</li><li>MapReduce\u5206\u5E03\u5F0F\u8BA1\u7B97\u6846\u67B6 (\u539F\u7406\uFF0C\u5E38\u7528\u7684\u7F16\u7A0B\u89C4\u8303\u7B49\uFF0C\u5927\u91CF\u6848\u4F8B\u7EC3\u4E60-\u5206\u533A\uFF0C\u6392\u5E8F(\u5FEB\u6392/\u5F52\u5E76\u6392\u5E8F)\uFF0C\u538B\u7F29\uFF0C\u81EA\u5B9A\u4E49\u7EC4\u4EF6\uFF0C\u7EFC\u5408\u6848\u4F8B)</li><li>Yarn \u8D44\u6E90\u8C03\u5EA6\u6846\u67B6(\u67B6\u6784\uFF0C\u539F\u7406\uFF0C\u4E09\u79CD\u8D44\u6E90\u8C03\u5EA6\u7B56\u7565)</li><li>Apache Hadoop\u6838\u5FC3\u6E90\u7801\u5256\u6790(nn\uFF0Cdn\u542F\u52A8\u6D41\u7A0B\u6E90\u7801\u5206\u6790\uFF0Cnn\u9AD8\u5E76\u53D1\u7684\u652F\u6491\u539F\u7406\uFF0C hadoop3.0\u65B0\u7279\u6027)</li><li>\u8C03\u4F18\u53CA\u4E8C\u6B21\u5F00\u53D1\u793A\u4F8B(hdfs,mr,yarn\u8C03\u4F18\uFF0Cnn\u5E38\u89C1GC\u95EE\u9898\uFF0C\u5B8C\u6210\u4E8C\u6B21\u5F00\u53D1\u7F16\u8BD1\u6E90\u7801)</li></ol><h2 id="_1-1-\u5927\u6570\u636E\u7684\u5B9A\u4E49" tabindex="-1"><a class="header-anchor" href="#_1-1-\u5927\u6570\u636E\u7684\u5B9A\u4E49" aria-hidden="true">#</a> 1.1 \u5927\u6570\u636E\u7684\u5B9A\u4E49</h2><p>\u5927\u6570\u636E\u662F\u6307\u65E0\u6CD5\u5728\u4E00\u5B9A\u65F6\u95F4\u8303\u56F4\u5185\u7528\u5E38\u89C4\u8F6F\u4EF6\u5DE5\u5177\u8FDB\u884C\u6355\u6349\u3001\u7BA1\u7406\u548C\u5904\u7406\u7684\u6570\u636E\u96C6\u5408\uFF0C \u662F\u9700\u8981\u65B0\u5904\u7406\u6A21\u5F0F\u624D\u80FD\u5177\u6709\u66F4\u5F3A\u7684\u51B3\u7B56\u529B\u3001\u6D1E\u5BDF\u53D1\u73B0\u529B\u548C\u6D41\u7A0B\u4F18\u5316\u80FD\u529B\u7684\u6D77\u91CF\u3001\u9AD8\u589E\u957F\u7387\u548C\u591A\u6837\u5316\u7684\u4FE1\u606F\u8D44\u4EA7\u3002</p><h2 id="_1-2-\u5927\u6570\u636E\u7684\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#_1-2-\u5927\u6570\u636E\u7684\u7279\u70B9" aria-hidden="true">#</a> 1.2 \u5927\u6570\u636E\u7684\u7279\u70B9</h2><p>\u5927\u6570\u636E\u7684\u7279\u70B9\u53EF\u4EE5\u7528 IBM \u66FE\u7ECF\u63D0\u51FA\u7684 \u201C5V\u201D \u6765\u63CF\u8FF0\uFF0C\u5982\u4E0B:</p><ul><li>Volume \uFF08\u5927\u91CF\uFF09</li><li>Velocity (\u9AD8\u901F)</li><li>Variety (\u591A\u6837)</li><li>Veracity (\u771F\u5B9E)</li><li>Value (\u4F4E\u4EF7\u503C)</li></ul><ol><li>\u5927\u91CF</li></ol><p>\u91C7\u96C6\u3001\u5B58\u50A8\u548C\u8BA1\u7B97\u7684\u6570\u636E\u91CF\u90FD\u975E\u5E38\u5927\u3002</p><p>\u8BA1\u7B97\u673A\u5B58\u50A8\u5355\u4F4D\u4E00\u822C\u7528B\uFF0CKB\uFF0CMB\uFF0CGB\uFF0CTB\uFF0CPB\uFF0CEB\uFF0CZB\uFF0CYB\uFF0CBB\u3001NB\u3001DB\u6765\u8868\u793A\uFF0C \u5B83\u4EEC\u4E4B\u95F4\u7684\u5173\u7CFB\u662F</p><p>1GB = 1024 MB\u30011TB = 1024 GB\u30011PB = 1024 TB\u30011EB = 1024 PB\u30011ZB = 1024 EB\u3001 1YB = 1024 ZB\u30011BB = 1024 YB\u30011NB = 1024 BB\u30011DB = 1024 NB</p><hr><p>\u4EE5PB\u4E3A\u4F8B\uFF0CPB\u7EA7\u6570\u636E\u91CF\u6709\u591A\u5927?\u662F\u600E\u6837\u7684\u4E00\u4E2A\u6982\u5FF5?</p><p>\u5047\u5982\u624B\u673A\u64AD\u653EMP3\u7684\u901F\u5EA6\u4E3A\u5E73\u5747\u6BCF\u5206\u949F1MB\uFF0C\u800C1\u9996\u6B4C\u66F2\u7684\u5E73\u5747\u65F6\u957F\u4E3A4\u5206\u949F\uFF0C\u90A3\u4E481PB\u5B58\u91CF\u7684\u6B4C\u66F2\u53EF\u4EE5\u8FDE\u7EED\u64AD\u653E2000\u5E74\u3002</p><p>1PB \u4E5F\u76F8\u5F53\u4E8E50%\u7684\u5168\u7F8E\u5B66\u672F\u7814\u7A76\u56FE\u4E66\u9986\u85CF\u4E66\u54A8\u8BE2\u5185\u5BB9\u3002</p><ul><li>1986\u5E74\uFF0C\u5168\u7403\u53EA\u67090.02EB\u4E5F\u5C31\u662F\u7EA621000TB\u7684\u6570\u636E\u91CF</li><li>2007\u5E74\uFF0C\u5168\u7403\u5C31\u662F280EB\u4E5F\u5C31\u662F\u7EA6300000000TB\u7684\u6570\u636E\u91CF\uFF0C\u7FFB\u4E8614000\u500D</li><li>\u8FD1\u4E9B\u5E74\uFF0C\u7531\u4E8E\u79FB\u52A8\u4E92\u8054\u7F51\u53CA\u7269\u8054\u7F51\u7684\u51FA\u73B0\uFF0C\u5404\u79CD\u7EC8\u7AEF\u8BBE\u5907\u7684\u63A5\u5165\uFF0C\u5404\u79CD\u4E1A\u52A1\u5F62\u5F0F\u7684\u666E \u53CA\uFF0C\u5E73\u5747\u6BCF40\u4E2A\u6708\uFF0C\u5168\u7403\u7684\u6570\u636E\u91CF\u5C31\u4F1A\u7FFB\u500D!2012\u5E74\uFF0C\u6BCF\u5929\u4F1A\u4EA7\u751F2.5EB\u7684\u6570\u636E\u91CF</li><li>\u57FA\u4E8EIDC\u7684\u62A5\u544A\u9884\u6D4B\uFF0C\u4ECE2013\u5E74\u52302020\u5E74\uFF0C\u5168\u7403\u6570\u636E\u91CF\u4F1A\u4ECE4.4ZB\u731B\u589E\u523044ZB!\u800C\u5230\u4E862025\u5E74\uFF0C\u5168\u7403\u4F1A\u6709163ZB\u7684\u6570\u636E\u91CF!</li></ul><p>\u5168\u7403\u7684\u6570\u636E\u91CF\u5DF2\u7ECF\u5927\u5230\u7206\u4E86!\u800C\u4F20\u7EDF\u7684\u5173\u7CFB\u578B\u6570\u636E\u5E93\u6839\u672C\u5904\u7406\u4E0D\u4E86\u5982\u6B64\u6D77\u91CF\u7684\u6570\u636E!</p><hr><ol start="2"><li>\u9AD8\u901F</li></ol><p>\u5728\u5927\u6570\u636E\u65F6\u4EE3\uFF0C\u6570\u636E\u7684\u521B\u5EFA\u3001\u5B58\u50A8\u3001\u5206\u6790\u90FD\u8981\u6C42\u88AB\u9AD8\u901F\u5904\u7406\uFF0C\u6BD4\u5982\u7535\u5546\u7F51\u7AD9\u7684\u4E2A\u6027\u5316\u63A8\u8350\u5C3D\u53EF\u80FD\u8981\u6C42\u5B9E\u65F6\u5B8C\u6210\u63A8\u8350\uFF0C\u8FD9\u4E5F\u662F\u5927\u6570\u636E\u533A\u522B\u4E8E\u4F20\u7EDF\u6570\u636E\u6316\u6398\u7684\u663E\u8457\u7279\u5F81\u3002</p><ol start="3"><li>\u591A\u6837</li></ol><p>\u6570\u636E\u5F62\u5F0F\u548C\u6765\u6E90\u591A\u6837\u5316\u3002\u5305\u62EC\u7ED3\u6784\u5316\u3001\u534A\u7ED3\u6784\u5316\u548C\u975E\u7ED3\u6784\u5316\u6570\u636E\uFF0C\u5177\u4F53\u8868\u73B0\u4E3A\u7F51\u7EDC\u65E5\u5FD7\u3001\u97F3\u9891\u3001\u89C6\u9891\u3001\u56FE\u7247\u3001\u5730\u7406\u4F4D\u7F6E\u4FE1\u606F\u7B49\u7B49\uFF0C\u591A\u7C7B\u578B\u7684\u6570\u636E\u5BF9\u6570\u636E\u7684\u5904\u7406\u80FD\u529B\u63D0\u51FA\u4E86\u66F4\u9AD8\u7684\u8981\u6C42\u3002</p><ol start="4"><li>\u771F\u5B9E</li></ol><p>\u786E\u4FDD\u6570\u636E\u7684\u771F\u5B9E\u6027\uFF0C\u624D\u80FD\u4FDD\u8BC1\u6570\u636E\u5206\u6790\u7684\u6B63\u786E\u6027\u3002</p><ol start="5"><li>\u4F4E\u4EF7\u503C</li></ol><p>\u6570\u636E\u4EF7\u503C\u5BC6\u5EA6\u76F8\u5BF9\u8F83\u4F4E\uFF0C\u6216\u8005\u8BF4\u662F\u6D6A\u91CC\u6DD8\u6C99\u5374\u53C8\u5F25\u8DB3\u73CD\u8D35\u3002</p><p>\u4E92\u8054\u7F51\u53D1\u5C55\u50AC\u751F\u4E86\u5927\u91CF\u6570\u636E\uFF0C\u4FE1\u606F\u6D77\u91CF\uFF0C\u4F46\u4EF7\u503C\u5BC6\u5EA6\u8F83\u4F4E\uFF0C\u5982\u4F55\u7ED3\u5408\u4E1A\u52A1\u903B\u8F91\u5E76\u901A\u8FC7\u5F3A\u5927\u7684\u673A\u5668\u7B97\u6CD5\u6765\u6316\u6398\u6570\u636E\u4EF7\u503C\uFF0C\u662F\u5927\u6570\u636E\u65F6\u4EE3\u6700\u9700\u8981\u89E3\u51B3\u7684\u95EE\u9898\uFF0C\u4E5F\u662F\u4E00\u4E2A\u6709\u96BE\u5EA6\u7684\u8BFE\u9898\u3002</p><h2 id="_1-3-\u5927\u6570\u636E\u7684\u5E94\u7528\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#_1-3-\u5927\u6570\u636E\u7684\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a> 1.3 \u5927\u6570\u636E\u7684\u5E94\u7528\u573A\u666F</h2><p>\u968F\u7740\u5927\u6570\u636E\u7684\u53D1\u5C55\uFF0C\u5927\u6570\u636E\u6280\u672F\u5DF2\u7ECF\u5E7F\u6CDB\u5E94\u7528\u5728\u4F17\u591A\u884C\u4E1A\uFF0C\u6BD4\u5982\u4ED3\u50A8\u7269\u6D41\u3001\u7535\u5546\u96F6\u552E\u3001\u6C7D\u8F66\u3001\u7535\u4FE1\u3001\u751F\u7269\u533B\u5B66\u3001\u4EBA\u5DE5\u667A\u80FD\u3001\u667A\u6167\u57CE\u5E02\u7B49\u7B49\u9886\u57DF\uFF0C\u5305\u62EC\u5728\u75AB\u60C5\u9632\u63A7\u6218\u4E2D\uFF0C\u5927\u6570\u636E\u6280\u672F\u4E5F\u53D1\u6325\u4E86\u91CD\u8981\u7684\u4F5C\u7528\u3002</p><ul><li>\u4ED3\u50A8\u7269\u6D41</li></ul><p>\u5927\u6570\u636E\u6280\u672F\u9A71\u52A8\u4E86\u4ED3\u50A8\u7269\u6D41\u9886\u57DF\u7684\u667A\u80FD\u5316\u53D1\u5C55\uFF0C\u4EE5\u82CF\u5B81\u4E3A\u4F8B\uFF0C\u82CF\u5B81\u7269\u6D41\u53EF\u5728\u5168\u56FD\u7684\u5404\u7EA7\u4ED3\u5E93\u95F4\u5B9E\u73B0\u667A\u80FD\u5206\u4ED3\u3001\u5C31\u8FD1\u5907\u8D27\u548C\u9884\u6D4B\u5F0F\u8C03\u62E8\uFF0C\u5B9E\u73B0&quot;\u5BA2\u6237\u9700\u8981\u7684\u5546\u54C1\u5C31\u5728\u79BB\u5BA2\u6237\u6700\u8FD1\u7684\u914D\u9001\u4E2D\u5FC3&quot;\u3002</p><ul><li>\u7535\u5546\u96F6\u552E</li></ul><p>\u96F6\u552E\u4E1A &quot;\u5564\u9152+\u7EB8\u5C3F\u88E4&quot; \u6848\u4F8B\u3002</p><ul><li><p>\u4E2A\u6027\u63A8\u8350</p></li><li><p>&quot;\u53CC11\u8D2D\u7269\u8282&quot; \u5B9E\u65F6\u9500\u552E\u989D\u5927\u5C4F</p></li><li><p>\u6C7D\u8F66</p></li></ul><p>\u5229\u7528\u4E86\u5927\u6570\u636E\u548C\u7269\u8054\u7F51\u6280\u672F\u7684\u65E0\u4EBA\u9A7E\u9A76\u6C7D\u8F66\uFF0C\u5728\u4E0D\u8FDC\u7684\u672A\u6765\u5C06\u8D70\u5165\u6211\u4EEC\u7684\u65E5\u5E38\u751F\u6D3B\u3002</p><ul><li>\u7535\u4FE1</li></ul><p>\u79FB\u52A8\u8054\u901A\u6839\u636E\u7528\u6237\u5E74\u9F84\u3001\u804C\u4E1A\u3001\u6D88\u8D39\u60C5\u51B5\uFF0C\u5206\u6790\u7EDF\u8BA1\u54EA\u79CD\u5957\u9910\u9002\u5408\u54EA\u7C7B\u4EBA\u7FA4\uFF0C\u5BF9\u5E02\u573A\u4EBA\u7FA4\u7CBE\u51C6\u5B9A\u5236\u3002</p><ul><li>\u751F\u7269\u533B\u5B66</li></ul><p>\u5927\u6570\u636E\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u5B9E\u73B0\u6D41\u884C\u75C5\u9884\u6D4B\u3001\u667A\u6167\u533B\u7597\u3001\u5065\u5EB7\u7BA1\u7406\uFF0C\u540C\u65F6\u8FD8\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u89E3\u8BFBDNA\uFF0C\u4E86\u89E3\u66F4\u591A\u7684\u751F\u547D\u5965\u79D8\u3002 \u6BD4\u5982\u5F71\u50CF\u5927\u6570\u636E\u652F\u6491\u4E0B\u7684\u65E9\u671F\u80BA\u764C\u652F\u6491\u5E73\u53F0\uFF0C\u57FA\u4E8E\u5927\u91CF\u75C5\u4F8B\u6570\u636E\u6837\u672C\uFF0C\u5236\u5B9A\u65E9\u671F\u80BA\u764C\u9AD8\u5371\u4EBA\u7FA4\u9884\u8B66\u6307\u6807\u3002</p><ul><li><p>\u4EBA\u5DE5\u667A\u80FD</p></li><li><p>\u667A\u6167\u57CE\u5E02</p></li></ul><p>\u5927\u6570\u636E\u6709\u6548\u652F\u6491\u667A\u6167\u57CE\u5E02\u53D1\u5C55\uFF0C\u6210\u4E3A\u57CE\u5E02\u7684&quot;\u6570\u636E\u5927\u8111&quot;\u3002</p><p><strong>\u5927\u6570\u636E\u7684\u4EF7\u503C\uFF0C\u8FDC\u8FDC\u4E0D\u6B62\u4E8E\u6B64\uFF0C\u5927\u6570\u636E\u5BF9\u5404\u884C\u5404\u4E1A\u7684\u6E17\u900F\uFF0C\u5927\u5927\u63A8\u52A8\u4E86\u793E\u4F1A\u751F\u4EA7\u548C\u751F\u6D3B\uFF0C\u672A\u6765\u5FC5\u5C06\u4EA7\u751F\u91CD\u5927\u800C\u6DF1\u8FDC\u7684\u5F71\u54CD\u3002</strong></p>',51);function o(e,r){return a}var t=l(p,[["render",o],["__file","index.html.vue"]]);export{t as default};
